import React, {useState} from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons'





const SearchBar = ()=>{
    const [query, setQuery]= useState("")
   const  handleSubmit = (e)=>{
    e.preventDefault();
    let searchQuery = e.target.value;
    console.log(searchQuery);
   }
    return (
        <div className="searchBarContainer">
        <form className="searchInput" onSubmit={handleSubmit} >
        <input type="search" className="searchBar" placeholder="Find Your City's Weather"/>
        <button type="submit" className="searchButton"><FontAwesomeIcon icon={faMagnifyingGlass} onChange={(e)=>setQuery(e.target.value)} value={query} /></button>
        </form>
        </div>
    )
};


<i class="fa-solid fa-magnifying-glass"></i>
export default SearchBar
